import React, { useRef } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { Points, PointMaterial } from '@react-three/drei';

function SpiralParticles() {
  const ref = useRef();
  const count = 2000;
  const positions = new Float32Array(count * 3);

  for (let i = 0; i < count; i++) {
    const t = i / count;
    const angle = t * Math.PI * 20;
    const radius = t * 5;

    positions[i * 3] = Math.cos(angle) * radius;
    positions[i * 3 + 1] = (t - 0.5) * 10;
    positions[i * 3 + 2] = Math.sin(angle) * radius;
  }

  useFrame((state) => {
    ref.current.rotation.y = state.clock.getElapsedTime() * 0.1;
  });

  return (
    <points ref={ref}>
      <bufferGeometry>
        <bufferAttribute
          attach="attributes-position"
          count={count}
          array={positions}
          itemSize={3}
        />
      </bufferGeometry>
      <PointMaterial
        transparent
        color="#bf00ff"
        size={0.03}
        sizeAttenuation={true}
        depthWrite={false}
      />
    </points>
  );
}

const SpiralBackground = () => {
  return (
    <div className="fixed top-0 left-0 w-full h-full -z-10">
      <Canvas camera={{ position: [0, 0, 8], fov: 75 }}>
        <SpiralParticles />
      </Canvas>
      <div className="absolute inset-0 bg-gradient-to-b from-transparent via-dark-bg/50 to-dark-bg pointer-events-none" />
    </div>
  );
};

export default SpiralBackground;
